INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (119351, 'Isavuconazonium', 'Etravirine', 'Moderate', 'Coadministration of etravirine with a drug, such as isavuconazonium sulfate (prodrug of isavuconazole), that is both a substrate as well as inhibitor of CYP450 3A4 may result in increased plasma concentrations of etravirine and decreased plasma concentrations of isavuconazole. Etravirine itself is a substrate and inducer of CYP450 3A4. Theoretically, etravirine may induce metabolism of isavuconazole while its own metabolism may be inhibited by the isavuconazole.', 'DDInter', 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with isavuconazonium sulfate. In addition, the potential for decreased efficacy of isavuconazole should be considered. Dosage adjustments may be required for one or both drugs.', 'Metabolism', 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with isavuconazonium sulfate.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119489/', '', 'Lamivudine, Glecaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', '', 1767369485);
