INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (119797, 'Isocarboxazid', 'Mepyramine', 'Moderate', 'Coadministration of monoamine oxidase inhibitors (MAOIs) and antihistamines may result in additive central nervous system depressant effects. In addition, limited data suggest that MAOIs may potentiate and prolong the anticholinergic effects of antihistamines due to inhibition of catecholamine degradation, which may lead to overstimulation of the sympathetic nervous system.', 'DDInter', 'Prescribing antihistamines in combination with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, procarbazine) should generally be avoided. If concomitant treatment is unavoidable, patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'Prescribing antihistamines in combination with MAOIs or other agents that possess MAOI activity (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119935/', '', 'Vilazodone, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, Bupropion', 'Lidocaine, Tetracaine, Ketotifen, Fexofenadine, Loratadine, Desloratadine, Azelastine, Terfenadine, Astemizole', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (119798, 'Isocarboxazid', 'Quazepam', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/119936/', '', 'Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, Vilazodone', 'Nitrazepam, Zopiclone', 1767369485);
