INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (120036, 'Isoprenaline', 'Metipranolol (ophthalmic)', 'Moderate', 'Some beta-blockers may antagonize the bronchodilatory, hypotensive, and tachycardic effects of isoproterenol. The mechanism is blockade of beta-adrenergic receptors, which leads to bronchoconstriction, vasodilation, and increased heart rate. Beta-blockers have been used successfully to treat catecholamine or isoproterenol-induced tachyarrhythmias.', 'DDInter', 'This combination should generally be avoided. Patients who are receiving isoproterenol for cardiac conditions should be closely monitored for adequate therapeutic effect if a beta blocker is added. If no alternative exists, small doses of a B-1 selective beta-blocker (e.g., acebutolol, atenolol, betaxolol, bisoprolol, or metoprolol) may be preferable; however, respiratory status should be closely monitored in patients with obstructive pulmonary disease.', 'Antagonism', 'This combination should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/120174/', '', 'Droxidopa, Amrinone, Angiotensin II, Milrinone, Methoxamine, Mometasone furoate, Umeclidinium, Budesonide, Glycopyrronium, Aclidinium, Mometasone, More', 'Sulbactam, Oxacillin, Dicloxacillin, Piperacillin, Meticillin, Carbenicillin, Bacampicillin, Ticarcillin, Mezlocillin, Tazobactam, Cloxacillin, More', 1767369485);
