INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (120465, 'Isosorbide mononitrate', 'Tizanidine', 'Major', 'Tizanidine may potentiate the hypotensive effect of some medications secondary to its alpha-2 adrenergic activity. Pharmacologic studies have found tizanidine to possess between 1/10 to 1/50 of the potency of clonidine, a structurally similar agent, in lowering blood pressure.', 'DDInter', 'A lower initial dosage and cautious dosage titration should be considered when tizanidine is initiated in patients receiving hypotensive medications. Although single doses of less than 8 mg of tizanidine have not been shown effective for spasticity in controlled clinical studies, it may be prudent to initiate treatment with 4 mg doses and gradually increase in 2 to 4 mg increments until optimum effect is achieved. The dose can be repeated at 6 to 8 hour intervals as needed, up to a maximum of three doses in 24 hours and a total daily dosage of 36 mg. However, experience with single doses exceeding 8 mg and daily doses exceeding 24 mg is limited. Close monitoring for development of hypotension is recommended.', 'Synergism', 'A lower initial dosage and cautious dosage titration should be considered when tizanidine is initiated in patients receiving hypotensive medications.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/120603/', '', 'Chlorzoxazone, Chlormezanone', 'Vericiguat', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (120466, 'Isosorbide mononitrate', 'Tranylcypromine', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the hypotensive effect of some medications. MAOIs alone quite commonly produce orthostatic hypotension. This effect may stem from a gradual MAOI-induced accumulation of false neurotransmitters in peripheral adrenergic neurons that have minimal activity at alpha- and beta-adrenergic receptors, resulting in a functional block of sympathetic neurotransmission. The interaction has been reported with the concomitant use of beta-blockers.', 'DDInter', 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment. Close monitoring for development of hypotension is recommended. Ambulatory patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/120604/', '', 'Desipramine, Vilazodone, Desvenlafaxine, St. John''s Wort, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, Protriptyline, More', 'Vericiguat', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (120467, 'Isosorbide mononitrate', 'Treprostinil', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', 'DDInter', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/120605/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, More', '', 1767369485);
