INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (120866, 'Itraconazole', 'Fosphenytoin', 'Moderate', 'Phenytoin and other hydantoins may reduce itraconazole serum levels by up to 90%. The mechanism appears to be enhanced first-pass metabolism and hepatic enzyme metabolism (CYP450 3A4) of itraconazole. Itraconazole therapy may fail. Ketoconazole is affected in a similar manner. In addition, itraconazole may increase the plasma concentrations of phenytoin.', 'DDInter', 'Coadministration of phenytoin or other hydantoins with itraconazole or ketoconazole should be avoided from 2 weeks before and during treatment with itraconazole or ketoconazole. Some experts suggest that if concomitant use is unavoidable, a dosage increase for itraconazole or ketoconazole should be considered. In addition, monitoring of phenytoin concentrations is recommended and the dosage adjusted if needed. Fluconazole is eliminated unchanged by the kidneys and may be a more appropriate antifungal agent, depending on the patient''s condition.', 'Metabolism', 'Coadministration of phenytoin or other hydantoins with itraconazole or ketoconazole should be avoided from 2 weeks before and during treatment with itraconazole or ketoconazole.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121004/', '', 'Micafungin, Amphotericin B, Posaconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Brivaracetam, Topiramate, Ezogabine, Paramethadione, Methsuximide, Fenfluramine, Eslicarbazepine, Lamotrigine, Phensuximide, Methylphenobarbital, Phenacemide, More', 1767369485);
