INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (120959, 'Itraconazole', 'Oxcarbazepine', 'Moderate', 'Coadministration with oxcarbazepine may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is accelerated clearance due to induction of CYP450 3A4 activity by oxcarbazepine.', 'DDInter', 'Caution is advised if oxcarbazepine must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever oxcarbazepine is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if oxcarbazepine must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121097/', '', 'Brivaracetam, Topiramate, Ezogabine, Paramethadione, Methsuximide, Fenfluramine, Eslicarbazepine, Lamotrigine, Phensuximide, Methylphenobarbital, Phenacemide, More', 'Micafungin, Amphotericin B, Miconazole, Posaconazole, Oteseconazole, Flucytosine, Anidulafungin', 1767369485);
