INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (120963, 'Itraconazole', 'Pantoprazole', 'Moderate', 'Proton pump inhibitors may decrease the gastrointestinal absorption of the azole antifungal agents, itraconazole and ketoconazole, both of which require an acidic environment for dissolution. By increasing gastric pH and reducing the acidity, proton pump inhibitors can decrease bioavailability of the azoles by 75% to 80%.', 'DDInter', 'If coadministration is necessary, an acidic pH may be produced with two capsules of glutamic acid hydrochloride administered 15 minutes before the azole dose. Administration with an acidic beverage such as Coca-Cola(R) or Pepsi(R) may also help. However, clinicians should still consider the possibility of a reduced or subtherapeutic antifungal effect. It may be appropriate to switch to an agent like fluconazole or terbinafine whose absorption is not affected by stomach pH.', 'Absorption', 'If coadministration is necessary, an acidic pH may be produced with two capsules of glutamic acid hydrochloride administered 15 minutes before the azole dose.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121101/', '', 'Tinidazole, Levofloxacin, Tetracycline, Misoprostol, Bismuth subcitrate potassium, Amoxicillin', 'Flucytosine, Anidulafungin, Micafungin, Oteseconazole', 1767369485);
