INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (121002, 'Itraconazole', 'Rifampicin', 'Major', 'Coadministration with rifamycins may significantly decrease the plasma concentrations of both itraconazole and ketoconazole. The proposed mechanism is accelerated clearance of the azole antifungal agent due to induction of CYP450 3A4-mediated metabolism by rifamycins.', 'DDInter', 'The manufacturers recommend that itraconazole and ketoconazole not be used concomitantly or within 2 weeks of treatment with rifamycins, unless the benefits outweigh the risk of potentially reduced antifungal efficacy. If coadministration is required, the antifungal activity should be monitored and the azole dosage increased as necessary. With rifabutin, however, consideration should also be given to the potential for increased risk of rifabutin toxicity, particularly when the azole dosage is increased. Therapeutic drug monitoring for rifabutin is therefore advisable, and the dosage adjusted if needed. In addition, a complete blood count should be performed at least weekly and as clinically indicated to monitor for development of neutropenia.', 'Metabolism', 'The manufacturers recommend that itraconazole and ketoconazole not be used concomitantly or within 2 weeks of treatment with rifamycins, unless the benefits outweigh the risk of potentially reduced antifungal efficacy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121140/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Cycloserine, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Micafungin, Miconazole, Oteseconazole, Flucytosine, Anidulafungin', 1767369485);
