INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (121006, 'Itraconazole', 'Ripretinib', 'Major', 'Coadministration of strong CYP450 3A4 inhibitors with ripretinib may increase the plasma concentrations and the risk of adverse effects (e.g., palmar-plantar erythrodysesthesia, hypertension, cardiac dysfunction, arthralgia/myalgia) of ripretinib and its active metabolite (DP-5439). The proposed mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ripretinib and DP-5439.', 'DDInter', 'Caution is recommended when ripretinib is used concomitantly with a strong CYP450 3A4 inhibitor. Clinical and laboratory monitoring should be considered whenever a strong CYP450 3A4 inhibitor is added to or withdrawn from therapy with ripretinib, and the dosage adjusted as necessary. Patients should be monitored for development of adverse effects.', 'Metabolism', 'Caution is recommended when ripretinib is used concomitantly with a strong CYP450 3A4 inhibitor.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121144/', '', 'Caspofungin, Micafungin, Amphotericin B, Oteseconazole, Flucytosine, Anidulafungin', 'Alpelisib, Lenvatinib, Trametinib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
