INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (121022, 'Itraconazole', 'Simeprevir', 'Major', 'Coadministration with potent and some moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of simeprevir, which is primarily metabolized by the isoenzyme.', 'DDInter', 'The use of simeprevir in combination with potent and some moderate CYP450 3A4 inhibitors such as azole antifungal agents, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided. Some authorities recommend avoiding concomitant use of simeprevir during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'The use of simeprevir in combination with potent and some moderate CYP450 3A4 inhibitors such as azole antifungal agents, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121160/', '', 'Caspofungin, Micafungin, Amphotericin B, Miconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Lactic acid, Methazolamide, Trichlormethiazide, Ciclopirox, Probenecid, Tioconazole, Brinzolamide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (121023, 'Itraconazole', 'Simvastatin', 'Major', 'Itraconazole increases the plasma concentrations of some HMG-CoA reductase inhibitors and may increase the risk of rhabdomyolysis. The mechanism has been reported to be inhibition of hepatic CYP450 3A4 enzymes responsible for HMG-CoA reductase inhibitor metabolism. Itraconazole greatly increases levels of lovastatin and its active metabolite, lovastatin acid and significantly increases simvastatin levels.', 'DDInter', 'The concurrent use of itraconazole with lovastatin and simvastatin is considered contraindicated by the manufacturer. Some authorities consider concomitant administration of lovastatin or simvastatin and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole. When concomitant use of an azole antifungal and an HMG-CoA reductase inhibitor is required, pravastatin which is not metabolized by CYP450 enzymes, or fluvastatin which is metabolized by CYP450 2C9, are recommended.', 'Metabolism', 'The concurrent use of itraconazole with lovastatin and simvastatin is considered contraindicated by the manufacturer.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121161/', '', 'Lixisenatide, Albiglutide, Guar gum, Acarbose, Pramlintide, Sitagliptin, Canagliflozin, Alogliptin, Liraglutide, Ertugliflozin, Semaglutide, More', 'Flucytosine, Anidulafungin, Micafungin, Oteseconazole', 1767369485);
