INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (121050, 'Itraconazole', 'Ticagrelor', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ticagrelor, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of ticagrelor with potent CYP450 3A4 inhibitors is considered contraindicated by some foreign authorities. The U.S. labeling for ticagrelor recommends avoiding use with potent CYP450 3A4 inhibitors. Labeling for itraconazole, a potent CYP450 3A4 inhibitor, specifically contraindicates use with ticagrelor during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of ticagrelor with potent CYP450 3A4 inhibitors is considered contraindicated by some foreign authorities.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121188/', '', 'Caspofungin, Micafungin, Amphotericin B, Oteseconazole, Flucytosine, Anidulafungin', 'Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (121051, 'Itraconazole', 'Tipranavir', 'Moderate', 'Coadministration with azole antifungal agents may substantially increase the plasma concentrations of tipranavir administered in combination with low-dose ritonavir. The proposed mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of both tipranavir and ritonavir.', 'DDInter', 'In patients treated with tipranavir/ritonavir, dosages of fluconazole, itraconazole, and ketoconazole exceeding 200 mg/day are not recommended. Due to multiple enzymes involved with voriconazole metabolism, the interaction is difficult to predict and no recommendations are available.', 'Metabolism', 'In patients treated with tipranavir/ritonavir, dosages of fluconazole, itraconazole, and ketoconazole exceeding 200 mg/day are not recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/121189/', '', 'Tenofovir alafenamide, Cabotegravir, Zanamivir, Lenacapavir, Velpatasvir, Oseltamivir, Brincidofovir, Famciclovir, Voxilaprevir, Tecovirimat, Maribavir, More', 'Micafungin, Caspofungin, Posaconazole, Oteseconazole, Flucytosine, Anidulafungin', 1767369485);
