INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122199, 'Citrus bioflavonoids', 'Warfarin', 'Moderate', 'Limited data suggest that flavonoids may affect the metabolism of warfarin. Various flavonoids including some flavones (e.g., 6-hydroxyflavone, 7-hydroxyflavone, chrysin, baicalein, apigenin, luteolin, scutellarein, wogonin) and flavonols (e.g., galangin, fisetin, kaempferol, morin, quercetin) have been found in vitro to inhibit CYP450 2C9, the isoenzyme responsible for the metabolic clearance of the biologically more active S(-) enantiomer of warfarin.', 'DDInter', 'The INR may need to be monitored more closely following addition or discontinuation of flavonoid preparations, and the warfarin dosage adjusted as necessary. The manufacturer recommends monitoring the INR one to two weeks after initiating Limbrel in patients anticoagulated with warfarin.', 'Metabolism', 'The INR may need to be monitored more closely following addition or discontinuation of flavonoid preparations, and the warfarin dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122337/', '', 'Ticagrelor, Betrixaban, Rivaroxaban, Cilostazol, Apixaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122200, 'Bismuth subsalicylate', 'Warfarin', 'Moderate', 'Theoretically, salicylates may potentiate the effects of anticoagulants and increase the risk of bleeding. Salicylates interfere with the action of vitamin K and induce a dose-dependent alteration in hepatic synthesis of coagulation factors VII, IX and X, occasionally increasing the prothrombin time. While these effects are generally slight for most salicylates (except aspirin) at recommended dosages, they may be of clinical significance when combined with the inhibitory effects of anticoagulants on the clotting cascade. Moreover, salicylates are known to cause dose-related gastrointestinal bleeding, which may be complicated by anticoagulant therapy.', 'DDInter', 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. The same precaution should be observed with the use of salicylate-related agents such as salicylamide because of their structural and pharmacological similarities.', 'Synergism', 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122338/', '', 'Alteplase, Dipyridamole, Caplacizumab, Cilostazol, Streptokinase, Defibrotide, Antithrombin III human, Tenecteplase, Protein C, Acenocoumarol, Selexipag, More', '', 1767369485);
