INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122240, 'Enzalutamide', 'Warfarin', 'Moderate', 'Apalutamide and Enzalutamide may reduce the effects of oral anticoagulants. The proposed mechanism is induction of the enzyme CYP450 2C9.', 'DDInter', 'Concomitant use of apalutamide or enzalutamide with warfarin and coumarin-like anticoagulants should be avoided. If concomitant therapy is required, additional INR monitoring should be conducted and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation, or change of dosage of enzalutamide in patients who are stabilized on their anticoagulant regimen. Patients should be monitored for altered anticoagulation and advised to promptly report any signs of blood clots (e.g., chest pain, shortness of breath, and sudden loss of vision, or pain, redness or swelling in an extremity) to their physician.', 'Metabolism', 'Concomitant use of apalutamide or enzalutamide with warfarin and coumarin-like anticoagulants should be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122378/', '', 'Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, More', 'Darolutamide, Abarelix, Relugolix, Degarelix, Nilutamide, Abiraterone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122241, 'Epoprostenol', 'Warfarin', 'Moderate', 'Prostacyclin/prostacyclin analogs (e.g., epoprostenol, iloprost, treprostinil) inhibit platelet aggregation and may theoretically potentiate the risk of bleeding in patients treated with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', 'DDInter', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122379/', '', 'Protein C, Acenocoumarol, Selexipag, Epoprostenol, Treprostinil', 'Antithrombin III human, Dicoumarol, Warfarin, Selexipag', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122242, 'Eptifibatide', 'Warfarin', 'Major', 'The risk of bleeding, including major bleeding (intracranial hemorrhage or a decrease in hemoglobin greater than 5 mg/dl) is significantly more likely when oral anticoagulants and glycoprotein IIb/IIIa inhibitors are given together.', 'DDInter', 'Some manufacturers of GP IIb/IIIa inhibitors consider their use contraindicated if patients have received oral anticoagulants within 7 days unless the prothrombin time is less than or equal to 1.2 times control. Bleeding precautions should be observed and patients should be closely monitored for clinical and laboratory evidence of adverse effects.', 'Synergism', 'Some manufacturers of GP IIb/IIIa inhibitors consider their use contraindicated if patients have received oral anticoagulants within 7 days unless the prothrombin time is less than or equal to 1.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122380/', '', 'Antithrombin III human, Dicoumarol, Warfarin, Selexipag', 'Eptifibatide, Antithrombin III human, Protein C, Acenocoumarol, Selexipag', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122243, 'Eravacycline', 'Warfarin', 'Moderate', 'Tetracyclines may increase the hypoprothrombinemic effects of warfarin and similar anticoagulants. The mechanism of interaction has not been established, but may involve depression of plasma prothrombin activity or suppression of vitamin K-producing bacteria in the gastrointestinal tract, although the latter may be of limited clinical importance except in patients whose dietary intake of vitamin K is severely reduced.', 'DDInter', 'Caution is advised when warfarin and similar anticoagulants are used with tetracyclines. The INR should be checked frequently and the anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of tetracycline therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Others', 'Caution is advised when warfarin and similar anticoagulants are used with tetracyclines.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122381/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122244, 'Erlotinib', 'Warfarin', 'Moderate', 'Erlotinib may potentiate the hypoprothrombinemic effect of warfarin and other oral anticoagulants. The mechanism of interaction is unknown, but may involve displacement of warfarin from protein-binding sites and/or competitive inhibition of R(+) warfarin metabolism via CYP450 3A4 and 1A2.', 'DDInter', 'Caution is advised during coadministration of warfarin and erlotinib. The INR should be checked frequently and warfarin dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of erlotinib in patients who are stabilized on their warfarin regimen. The same precaution may be applicable during therapy with other oral anticoagulants, although clinical data are lacking. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Metabolism, Distribution', 'Caution is advised during coadministration of warfarin and erlotinib.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122382/', '', 'Entrectinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Bosutinib, Lapatinib, Ribociclib, Cobimetinib, Afatinib, More', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122245, 'Escitalopram', 'Warfarin', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122383/', '', 'Protein C, Acenocoumarol, Selexipag', 'Phenelzine, Tranylcypromine, Maprotiline, Isocarboxazid, Oxitriptan, Tryptophan, Esketamine', 1767369485);
