INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122259, 'Etoposide', 'Warfarin', 'Moderate', 'The combination of etoposide and carboplatin has been reported to potentiate the hypoprothrombinemic effect of warfarin in a patient stabilized on the anticoagulant. The exact mechanism of the interaction is unknown but may be related to protein binding displacement of warfarin.', 'DDInter', 'Based on these observations, appropriate monitoring for excessive hypoprothrombinemia is recommended early on and later in time course following the addition of etoposide and carboplatin to a stabilized regimen of warfarin. The same precaution may be applicable during therapy with other oral anticoagulants, since these agents are all highly protein-bound in the plasma. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Distribution', 'Based on these observations, appropriate monitoring for excessive hypoprothrombinemia is recommended early on and later in time course following the addition of etoposide and carboplatin to a stabilized regimen of warfarin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122397/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, More', 'Teniposide, Trabectedin, Cabazitaxel', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122260, 'Etravirine', 'Warfarin', 'Moderate', 'Coadministration with etravirine may increase the plasma concentrations of warfarin. The proposed mechanism is etravirine inhibition of CYP450 2C19 and 2C9, the isoenzymes responsible for the metabolic clearance of the R(+) and S(-) enantiomers of warfarin, respectively.', 'DDInter', 'Given the potential for interaction and the high degree of interpatient variability with respect to warfarin metabolism, patients should be closely monitored during concomitant therapy with etravirine. The INR should be checked frequently and warfarin dosage adjusted accordingly, particularly following initiation or discontinuation of etravirine in patients who are stabilized on their warfarin regimen. The same precaution may be applicable during therapy with other oral anticoagulants, although clinical and pharmacokinetic data are lacking. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Metabolism', 'Given the potential for interaction and the high degree of interpatient variability with respect to warfarin metabolism, patients should be closely monitored during concomitant therapy with etravirine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122398/', '', 'Betrixaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, More', 'Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zana