INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122261, 'Exenatide', 'Warfarin', 'Moderate', 'Spontaneous reports of increased INR, sometimes accompanied by bleeding, have been associated with concomitant use of warfarin and exenatide. The mechanism of interaction is unknown.', 'DDInter', 'Until more data are available, caution is advised if exenatide is prescribed to patients treated with warfarin. The INR should be checked frequently and warfarin dosage adjusted accordingly, particularly following initiation or discontinuation of exenatide in patients who are stabilized on their warfarin regimen. The same precaution may be applicable during therapy with other oral anticoagulants, although clinical data are lacking. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Others', 'Until more data are available, caution is advised if exenatide is prescribed to patients treated with warfarin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122399/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', 'Acetohexamide, Guar gum, Pramlintide, Sitagliptin, Alogliptin, Liraglutide, Ertugliflozin, Semaglutide, Linagliptin, Tolazamide, Empagliflozin, More', 1767369485);
