INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122289, 'Glipizide', 'Warfarin', 'Moderate', 'Oral sulfonylureas may enhance or reduce the hypoprothrombinemic response to oral anticoagulants. The mechanism may be related to displacement from plasma protein binding sites. In addition, coumarin anticoagulants may cause an increase in blood levels of hypoglycemic agents, possibly by inhibiting their hepatic metabolism. Clinical data have been highly variable.', 'DDInter', 'The patient should be monitored for altered anticoagulation (PT/INR) and altered glycemic effect when either of these drugs is added to or removed from a patient''s regimen. Patients should be advised to regularly monitor their blood sugar, counseled on how to recognize and treat hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, tremor, hunger, weakness, or palpitations), and to promptly report any signs of bleeding (pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools) to their physician.', 'Metabolism', 'The patient should be monitored for altered anticoagulation (PT/INR) and altered glycemic effect when either of these drugs is added to or removed from a patient''s regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122427/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, More', 'Acetohexamide, Guar gum, Pramlintide, Sitagliptin, Alogliptin, Liraglutide, Ertugliflozin, Semaglutide, Linagliptin, Tolazamide, Empagliflozin, More', 1767369485);
