INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122297, 'Hemin', 'Warfarin', 'Moderate', 'Theoretically, hemin may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Hemin has exhibited mild, transient anticoagulant effects during clinical studies.', 'DDInter', 'The use of hemin with concurrent anticoagulant therapy should be avoided. Caution is advised if hemin is used in combination with other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'The use of hemin with concurrent anticoagulant therapy should be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122435/', '', 'Protein C, Acenocoumarol, Selexipag, Desirudin', 'Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Human C1-esterase inhibitor, Berotralstat, Lanadelumab, Icatibant, Crizanlizumab', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122298, 'Heparin', 'Warfarin', 'Major', 'Although therapy with heparin and oral anticoagulants often overlap in clinical practice, the potential for additive anticoagulant effects and increased risk of severe bleeding should be considered. Oral anticoagulants may prolong the activated partial thromboplastin time (aPTT) in patients receiving heparin, while heparin may prolong the International Normalized Ratio (INR) in patients receiving warfarin.', 'DDInter', 'During concurrent or overlapping therapy, close clinical and laboratory observation for hematologic complications is recommended. To minimize the interference of heparin on INR determinations, blood should not be drawn until at least 5 hours after the last intravenous heparin dose or 24 hours after the last subcutaneous heparin dose.', 'Synergism', 'During concurrent or overlapping therapy, close clinical and laboratory observation for hematologic complications is recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122436/', '', 'Selexipag, Heparin', 'Antithrombin III human, Dicoumarol, Warfarin, Selexipag, Vitamin A, Potassium Iodide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122299, 'Hydroflumethiazide', 'Warfarin', 'Minor', 'Thiazide diuretics may reduce intravascular volume. Higher plasma levels of clotting factors and decreased anticoagulant effect may result; however the effects may not be clinically significant. Management consists of monitoring coagulation parameters during the initial phase of coadministration. Patients should be advised to promptly report any signs of blood clots (e.g. chest pain, shortness of breath, sudden loss of vision, or pain, redness or swelling in an extremity).', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122437/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, More', 'Methazolamide, Diazoxide, Econazole, Diclofenamide, Mafenide, Tioconazole, Furazolidone, Lactic acid, Povidone-iodine, Diiodohydroxyquinoline', 1767369485);
