INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122323, 'Warfarin', 'Larotrectinib', 'Moderate', 'Coadministration with larotrectinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.', 'DDInter', 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever larotrectinib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. Some authorities recommend avoiding concomitant use of larotrectinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122461/', '', 'Betrixaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, More', 'Entrectinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Bosutinib, Lapatinib, Ribociclib, Cobimetinib, Afatinib, More', 1767369485);
