INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122329, 'Warfarin', 'Levamisole', 'Moderate', 'Significantly increased INR and prothrombin times have been reported in patients receiving concomitant warfarin, fluorouracil, and levamisole. Discontinuation of chemotherapy resulted in subtherapeutic anticoagulation. Reinstitution of chemotherapy resulted in a clinically significant elevation in the INR. The mechanism of this interaction is unknown and causality has not been clearly established.', 'DDInter', 'If concomitant therapy is necessary, close monitoring of the INR or PT is recommended. Anticoagulant dose adjustments may be necessary. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, nosebleeds, bleeding of gums from brushing, red or brown urine, red or black stools, or unusual bleeding or bruising.', 'Others', 'If concomitant therapy is necessary, close monitoring of the INR or PT is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122467/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', 'Thiabendazole, Pyrantel, Piperazine, Mebendazole, Albendazole', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122330, 'Warfarin', 'Levofloxacin', 'Major', 'Some quinolone antibiotics have been reported to potentiate the hypoprothrombinemic effect of warfarin and other coumarin anticoagulants. The exact mechanism is unknown but may involve inhibition of coumarin metabolism and/or depletion of certain clotting factors due to suppression of vitamin K-producing intestinal flora.', 'DDInter', 'Given the potential for clinically significant interaction and even fatality in the occasional, susceptible patient, close monitoring is recommended if a quinolone antibiotic is prescribed during coumarin anticoagulant therapy. The INR should be checked frequently and coumarin dosage adjusted accordingly, particularly following initiation or discontinuation of quinolone therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools. The same precaution may be applicable during therapy with other oral anticoagulants (e.g., indandiones), although clinical data are lacking.', 'Others', 'Given the potential for clinically significant interaction and even fatality in the occasional, susceptible patient, close monitoring is recommended if a quinolone antibiotic is prescribed during coumarin anticoagulant therapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122468/', '', 'Ticagrelor, Betrixaban, Clopidogrel, Apixaban, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, More', 'Delafloxacin, Vonoprazan, Bismuth subcitrate potassium, Amikacin, Secnidazole, Ganciclovir, Rifamycin, Kanamycin, Netilmicin, Polymyxin B, Trifluridine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122331, 'Warfarin', 'Levomilnacipran', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122469/', '', 'Protein C, Acenocoumarol, Selexipag', 'Phenelzine, Tranylcypromine, Maprotiline, Isocarboxazid, Oxitriptan, Tryptophan, Esketamine', 1767369485);
