INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122354, 'Warfarin', 'Mezlocillin', 'Moderate', 'Penicillins may occasionally potentiate the risk of bleeding in patients treated with oral anticoagulants. The exact mechanism of interaction is unknown but may involve penicillin inhibition of platelet aggregation.', 'DDInter', 'Caution is recommended if a penicillin is prescribed during oral anticoagulant therapy, especially in the elderly and patients with uremia or hepatic impairment. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of penicillin therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is recommended if a penicillin is prescribed during oral anticoagulant therapy, especially in the elderly and patients with uremia or hepatic impairment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122492/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', 'Tazobactam, Sulbactam', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122355, 'Warfarin', 'Miconazole', 'Major', 'Systemically or even topically administered miconazole may increase the plasma concentrations and hypoprothrombinemic effect of warfarin. The proposed mechanism is miconazole inhibition of CYP450 2C9, the isoenzyme responsible for the metabolic clearance of the more active S(-) enantiomer of warfarin.', 'DDInter', 'Patients receiving warfarin or other oral anticoagulants should be closely monitored during concomitant therapy with miconazole. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of miconazole therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools. Some authorities consider the use of oral miconazole with warfarin to be contraindicated. Although vaginally administered miconazole is generally less than 2% absorbed in healthy women of childbearing age, increased absorption may occur in the presence of atrophic vaginal epithelium. Therefore, the same precaution is applicable when vaginal formulations of miconazole is prescribed to women receiving oral anticoagulant therapy.', 'Metabolism', 'Patients receiving warfarin or other oral anticoagulants should be closely monitored during concomitant therapy with miconazole.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122493/', '', 'Betrixaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, More', 'Rifamycin, Kanamycin, Polymyxin B, Streptomycin, Fidaxomicin, Paromomycin, Flucytosine, Anidulafungin, Micafungin, Oteseconazole, Thiabendazole, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122356, 'Warfarin', 'Mifepristone', 'Major', 'Concomitant use of anticoagulants may exacerbate mifepristone-induced vaginal bleeding in women. Prolonged, potentially heavy bleeding lasting approximately 9 to 30 days typically follows mifepristone use in termination of pregnancy. Because it is a progesterone receptor antagonist, mifepristone can also promote unopposed endometrial proliferation that may result in endometrium thickening, cystic dilatation of endometrial glands, and vaginal bleeding when used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome.', 'DDInter', 'Concurrent anticoagulant therapy is considered a contraindication in the use of mifepristone for pregnancy termination. When used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, mifepristone treatment should be administered cautiously in women who are receiving anticoagulant therapy. Patients who experience vaginal bleeding should be referred to a gynecologist for further evaluation.', 'Synergism', 'Concurrent anticoagulant therapy is considered a contraindication in the use of mifepristone for pregnancy termination.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122494/', '', 'Alteplase, Dipyridamole, Abciximab, Tirofiban, Caplacizumab, Acetylsalicylic acid, Iloprost, Streptokinase, Eptifibatide, Antithrombin III human, Tenecteplase, More', 'Ospemifene, Ulipristal, Prasterone, Bazedoxifene', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122357, 'Warfarin', 'Milnacipran', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122495/', '', 'Protein C, Acenocoumarol, Selexipag', 'Phenelzine, Tranylcypromine, Maprotiline, Isocarboxazid, Oxitriptan, Tryptophan, Esketamine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122358, 'Warfarin', 'Mirabegron', 'Minor', 'Coadministration with mirabegron has been shown to slightly alter the pharmacokinetics of single-dose warfarin. Mirabegron had no effect on warfarin pharmacodynamic endpoints such as International Normalized Ratio (INR) and prothrombin time. However, the effect of mirabegron on multiple doses of warfarin has not been fully investigated.', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122496/', '', 'Betrixaban, Clopidogrel, Cilostazol, Apixaban, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, More', 'Apomorphine, Fesoterodine, Phenazopyridine, Acetohydroxamic acid, Trospium, Yohimbine, Flavoxate, Avanafil, Papaverine, Magnesium hydroxide, Ammonium chloride, More', 1767369485);
