INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122362, 'Warfarin', 'Moricizine', 'Minor', 'According to one case report, prothrombin time was elevated in a patient who was stable on warfarin and who then received moricizine. Controlled studies, however, have revealed only that serum warfarin half-life is decreased during coadministration. These studies have found no significant change in the prothrombin time. Similar effects may occur with other oral anticoagulants. Management consists of following the patient''s prothrombin time or INR during coadministration. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122500/', '', 'Tocainide, Bretylium, Ibutilide', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', 1767369485);
