INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122395, 'Warfarin', 'Penbutolol', 'Minor', 'Some oral beta-blockers may increase serum levels of oral anticoagulants and enhance anticoagulant effects. The mechanism is unknown . Data have been conflicting. Case reports exist for propranolol, while acebutolol, atenolol, betaxolol, bisoprolol, carvedilol, esmolol, metoprolol, and pindolol reportedly do not interact. Hypoprothrombinemic effects are expected to be minimal. It is recommended that PT or INR be monitored when a beta-blocker is added to or deleted from the medical regimen of a patient receiving an oral anticoagulant. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122533/', '', 'Betrixaban, Clopidogrel, Cilostazol, Apixaban, Rivaroxaban, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, More', 'Esmolol, Nebivolol, Carteolol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122396, 'Warfarin', 'Benzylpenicillin (sodium)', 'Moderate', 'Penicillins may occasionally potentiate the risk of bleeding in patients treated with oral anticoagulants. The exact mechanism of interaction is unknown but may involve penicillin inhibition of platelet aggregation.', 'DDInter', 'Caution is recommended if a penicillin is prescribed during oral anticoagulant therapy, especially in the elderly and patients with uremia or hepatic impairment. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of penicillin therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is recommended if a penicillin is prescribed during oral anticoagulant therapy, especially in the elderly and patients with uremia or hepatic impairment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122534/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', 'Tazobactam, Sulbactam, Ganciclovir, Rifamycin, Kanamycin, Netilmicin, Polymyxin B, Trifluridine, Amikacin, Povidone-iodine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122397, 'Warfarin', 'Pentobarbital', 'Major', 'Barbiturates reduce the effects of oral anticoagulants by inducing their hepatic metabolism. Anticoagulant dosage requirements may be increased by 30% to 60%. After the barbiturate is discontinued, excessive anticoagulation and bleeding may occur if the anticoagulant dose is not reduced.', 'DDInter', 'Close attention should be paid to alterations in coagulation if a patient must take both drugs and for several weeks after the dose of the barbiturate is changed or discontinued. Alternatively, a benzodiazepine might be used instead of a barbiturate, if appropriate. Patients should be advised to promptly report any signs of blood clots (e.g. chest pain, shortness of breath, sudden loss of vision, or pain, redness or swelling in an extremity).', 'Metabolism', 'Close attention should be paid to alterations in coagulation if a patient must take both drugs and for several weeks after the dose of the barbiturate is changed or discontinued.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122535/', '', 'Remimazolam, Suvorexant, Quazepam, Propiomazine, Ramelteon, Scopolamine, Eszopiclone, Lemborexant, Tasimelteon, Dichloralphenazone, Valerian', 'Betrixaban, Clopidogrel, Cilostazol, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, More', 1767369485);
