INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122401, 'Warfarin', 'Phenytoin', 'Moderate', 'Some oral anticoagulants have been reported to increase phenytoin half-life and serum concentrations, possibly by inhibiting CYP450 metabolism. The addition of phenytoin to warfarin therapy has initially increased prothrombin time, but thereafter reduced anticoagulant effects. The mechanism is not known, but may be due to initial CYP450 metabolic inhibition, followed by isoenzyme induction.', 'DDInter', 'Phenytoin serum concentrations and INR/PT should be monitored in patients receiving this combination and whenever the dosage is changed or discontinued. Patients should be advised to promptly report any signs of bleeding (e.g., pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools) or hydantoin toxicity (e.g., drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia) to their physician.', 'Metabolism', 'Phenytoin serum concentrations and INR/PT should be monitored in patients receiving this combination and whenever the dosage is changed or discontinued.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122539/', '', 'Tiagabine, Methsuximide, Ezogabine, Cenobamate, Paramethadione, Perampanel, Phensuximide, Ganaxolone, Phenacemide, Trimethadione, Ethosuximide, More', 'Cilostazol, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122402, 'Warfarin', 'Piroxicam', 'Major', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the hypoprothrombinemic effect and bleeding risk associated with oral anticoagulants.', 'DDInter', 'NSAIDs should be administered with oral anticoagulants only if benefit outweighs risk. The INR should be checked frequently and oral anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of NSAIDs in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of unusual bleeding or bruising to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools. Salicylates (except aspirin) appear to have less effect on coagulation and may be preferable in patients treated with oral anticoagulants.', 'Synergism', 'NSAIDs should be administered with oral anticoagulants only if benefit outweighs risk.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122540/', '', 'Tolazoline, Dimethyl sulfoxide, Fluocinolone acetonide, Fluorometholone', 'Protein C, Acenocoumarol, Selexipag', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122403, 'Warfarin', 'Pitavastatin', 'Minor', 'According to the product labeling, pitavastatin 4 mg daily had no significant effect on the pharmacokinetics of R(+) and S(-) warfarin or the steady-state pharmacodynamics of warfarin in healthy volunteers. Pitavastatin also did not affect prothrombin time (PT) or international normalized ratio (INR) when administered to patients receiving chronic warfarin treatment. However, PT or INR should be monitored when pitavastatin is added to warfarin therapy. The same precaution may also be applicable to other oral anticoagulants.', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122541/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', 'Mipomersen, Evinacumab, Evolocumab, Probucol, Bempedoic acid, Alirocumab', 1767369485);
