INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122405, 'Warfarin', 'Polythiazide', 'Minor', 'Thiazide diuretics may reduce intravascular volume. Higher plasma levels of clotting factors and decreased anticoagulant effect may result; however the effects may not be clinically significant. Management consists of monitoring coagulation parameters during the initial phase of coadministration. Patients should be advised to promptly report any signs of blood clots (e.g. chest pain, shortness of breath, sudden loss of vision, or pain, redness or swelling in an extremity).', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122543/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, More', 'Methazolamide, Diazoxide, Econazole, Diclofenamide, Mafenide, Tioconazole, Furazolidone, Lactic acid, Povidone-iodine, Diiodohydroxyquinoline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122406, 'Warfarin', 'Ponatinib', 'Major', 'Coadministration of ponatinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with ponatinib is associated with severe, sometimes fatal hemorrhage including cerebral and gastrointestinal hemorrhage.', 'DDInter', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with ponatinib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The INR should be monitored more frequently during coadministration of warfarin. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with ponatinib.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122544/', '', 'Protein C, Acenocoumarol, Selexipag', 'Entrectinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Bosutinib, Lapatinib, Ribociclib, Cobimetinib, Afatinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122407, 'Warfarin', 'Posaconazole', 'Moderate', 'Azole antifungal agents that are potent inhibitors of CYP450 3A4 such as itraconazole, ketoconazole, and posaconazole may increase the plasma concentrations and hypoprothrombinemic effect of warfarin. The proposed mechanism is inhibition of CYP450 3A4-mediated metabolism of R(+) warfarin, the less biologically active of the two enantiomers of warfarin.', 'DDInter', 'Patients receiving warfarin should be closely monitored during concomitant therapy with azole antifungal agents that are potent inhibitors of CYP450 3A4. The INR should be checked frequently and warfarin dosage adjusted accordingly, particularly following initiation, change of dosage, or discontinuation of azole antifungal therapy. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Metabolism', 'Patients receiving warfarin should be closely monitored during concomitant therapy with azole antifungal agents that are potent inhibitors of CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122545/', '', 'Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, More', 'Flucytosine, Anidulafungin, Micafungin, Oteseconazole', 1767369485);
