INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122409, 'Warfarin', 'Pravastatin', 'Minor', 'Pravastatin has not been reported to affect the response to warfarin. However, some other HMG-CoA reductase inhibitors have been shown to lead to an enhanced hypoprothrombinemic response. Some patients have developed bleeding (i.e., hematuria, epistaxis). The clinical significance of this interaction has not been fully evaluated. Patients should be monitored for altered hypoprothrombinemic response to warfarin or other oral anticoagulants when pravastatin dosage is added, discontinued or changed. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122547/', '', 'Mipomersen, Evinacumab, Evolocumab, Probucol, Bempedoic acid, Alirocumab, Bempedoic acid', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, More', 1767369485);
