INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122458, 'Warfarin', 'St. John''s Wort', 'Moderate', 'Limited clinical data suggest that St. John''s wort may reduce the plasma concentrations and/or hypoprothrombinemic effect of warfarin and other oral anticoagulants. The proposed mechanism is induction of CYP450 isoenzymes by constituents of St. John''s wort.', 'DDInter', 'In general, patients should consult a healthcare provider before taking any herbal or alternative medicine. Given the potential for interaction and the narrow therapeutic index of oral anticoagulants, these drugs should preferably not be used with St. John''s wort. If they are given together, the INR must be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of St. John''s wort in patients who are stabilized on their anticoagulant regimen.', 'Metabolism', 'In general, patients should consult a healthcare provider before taking any herbal or alternative medicine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122596/', '', 'Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, More', 'Phenelzine, Tranylcypromine, Maprotiline, Isocarboxazid, Oxitriptan, Tryptophan, Esketamine', 1767369485);
