INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122469, 'Warfarin', 'Sulindac', 'Major', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the hypoprothrombinemic effect and bleeding risk associated with oral anticoagulants.', 'DDInter', 'NSAIDs should be administered with oral anticoagulants only if benefit outweighs risk. The INR should be checked frequently and oral anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of NSAIDs in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of unusual bleeding or bruising to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools. Salicylates (except aspirin) appear to have less effect on coagulation and may be preferable in patients treated with oral anticoagulants.', 'Synergism', 'NSAIDs should be administered with oral anticoagulants only if benefit outweighs risk.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122607/', '', 'Protein C, Acenocoumarol, Selexipag', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122470, 'Warfarin', 'Sunitinib', 'Moderate', 'The concomitant use of anticoagulants may theoretically increase the risk of sunitinib-induced bleeding. Severe and life-threatening tumor hemorrhage, hemoptysis, and pulmonary hemorrhage, and nonserious epistaxis have occurred during treatment with sunitinib.', 'DDInter', 'Monitoring for clinical and laboratory signs of bleeding is recommended during concomitant therapy, including INR/PT and complete blood counts. Patients should be advised to promptly report any potential signs of bleeding to their doctor, including nosebleeds, pain, swelling, or headache.', 'Synergism', 'Monitoring for clinical and laboratory signs of bleeding is recommended during concomitant therapy, including INR/PT and complete blood counts.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122608/', '', 'Ticagrelor, Clopidogrel, Iloprost, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Prasugrel, Streptokinase, Eptifibatide, More', 'Entrectinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Bosutinib, Lapatinib, Ribociclib, Cobimetinib, Afatinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122471, 'Warfarin', 'Suprofen (ophthalmic)', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', 'DDInter', 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122609/', '', 'Protein C, Acenocoumarol, Selexipag', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122472, 'Warfarin', 'Tazemetostat', 'Moderate', 'Coadministration with tazemetostat may decrease the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The proposed mechanism is increased clearance due to induction of CYP450 3A4 by tazemetostat.', 'DDInter', 'Caution is advised when tazemetostat is used concomitantly with drugs that undergo metabolism by CYP450 3A4. Dose adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever tazemetostat is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when tazemetostat is used concomitantly with drugs that undergo metabolism by CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122610/', '', 'Clopidogrel, Cilostazol, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Argatroban, Desirudin, Eptifibatide, More', 'Pertuzumab, Olaparib, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Porfimer sodium, Selinexor, Aflibercept, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122473, 'Warfarin', 'Telaprevir', 'Moderate', 'Liver function changes due to treatment with direct-acting HCV antiviral agents may affect the therapeutic efficacy of vitamin K antagonists. The mechanism may involve potential pharmacokinetic changes for some vitamin K antagonists due to altered metabolism, or patients may exhibit alterations in hepatic synthesis of vitamin K-dependent coagulation factors and clearance of activated factors that could impact response to vitamin K antagonists.', 'DDInter', 'The INR should be monitored more closely following initiation or discontinuation of a direct-acting HCV antiviral agent, and the dosage of vitamin K antagonist adjusted if necessary.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122611/', '', 'Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, More', 'Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Remdesivir, Doravirine, Bictegravir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122474, 'Warfarin', 'Telithromycin', 'Moderate', 'Coadministration with telithromycin may infrequently enhance the hypoprothrombinemic effect of warfarin. The exact mechanism of interaction is unknown. Although telithromycin is a potent inhibitor of CYP450 3A4 and can inhibit metabolism of the R(+) enantiomer of warfarin, the overall effect on racemic warfarin pharmacokinetics appears to be minor.', 'DDInter', 'Given the potential for clinically significant interaction in the occasional, susceptible patient, close monitoring is recommended if telithromycin is prescribed during warfarin therapy. The INR should be checked frequently and coumarin dosage adjusted accordingly, particularly following initiation or discontinuation of telithromycin therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools. The same precaution may be applicable during therapy with other oral anticoagulants, although clinical data are lacking.', 'Metabolism', 'Given the potential for clinically significant interaction in the occasional, susceptible patient, close monitoring is recommended if telithromycin is prescribed during warfarin therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122612/', '', 'Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, Streptokinase, More', 'Dirithromycin', 1767369485);
