INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122481, 'Warfarin', 'Thiopental', 'Major', 'Barbiturates reduce the effects of oral anticoagulants by inducing their hepatic metabolism. Anticoagulant dosage requirements may be increased by 30% to 60%. After the barbiturate is discontinued, excessive anticoagulation and bleeding may occur if the anticoagulant dose is not reduced.', 'DDInter', 'Close attention should be paid to alterations in coagulation if a patient must take both drugs and for several weeks after the dose of the barbiturate is changed or discontinued. Alternatively, a benzodiazepine might be used instead of a barbiturate, if appropriate. Patients should be advised to promptly report any signs of blood clots (e.g. chest pain, shortness of breath, sudden loss of vision, or pain, redness or swelling in an extremity).', 'Metabolism', 'Close attention should be paid to alterations in coagulation if a patient must take both drugs and for several weeks after the dose of the barbiturate is changed or discontinued.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122619/', '', 'Alfentanil, Remifentanil, Sodium oxybate, Isoflurane, Nitrous oxide, Desflurane, Sufentanil, Enflurane, Esketamine, Halothane, Ketamine, More', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122482, 'Warfarin', 'Ticagrelor', 'Moderate', 'The concomitant use of ticagrelor with other platelet inhibitors, anticoagulants, thrombolytic agents, or nonsteroidal anti-inflammatory drugs may increase the risk of bleeding.', 'DDInter', 'Caution is recommended when these drugs are used concomitantly with ticagrelor or within 24 hours of ticagrelor dosing. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is recommended when these drugs are used concomitantly with ticagrelor or within 24 hours of ticagrelor dosing.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122620/', '', 'Ticagrelor, Protein C, Acenocoumarol, Selexipag, Cangrelor', 'Antithrombin III human, Dicoumarol, Warfarin, Selexipag', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122483, 'Warfarin', 'Ticlopidine', 'Moderate', 'Although therapy with oral anticoagulants and ticlopidine may overlap in clinical practice, the potential for additive pharmacodynamic effects and increased risk of bleeding, especially gastrointestinal bleeding, should be considered. The combination of warfarin and ticlopidine has also been associated with rare cases of cholestatic hepatitis. In addition, ticlopidine may inhibit the metabolism of R-warfarin by an unknown mechanism. Increases in R-warfarin concentrations have been reported during concomitant therapy; however, there were no significant changes in INR.', 'DDInter', 'The manufacturer recommends discontinuation of any anticoagulants before initiating ticlopidine therapy. If concomitant use cannot be avoided, patients should be closely monitored for signs of bleeding and the anticoagulant dosage should be adjusted as appropriate to maintain therapeutic aPTT/INR values. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bruising, red or brown urine, or red or black stools.', 'Metabolism', 'The manufacturer recommends discontinuation of any anticoagulants before initiating ticlopidine therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122621/', '', 'Antithrombin III human, Dicoumarol, Warfarin, Selexipag', 'Dipyridamole, Antithrombin III human, Protein C, Acenocoumarol, Selexipag, Ticlopidine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122484, 'Warfarin', 'Timolol', 'Minor', 'Some oral beta-blockers may increase serum levels of oral anticoagulants and enhance anticoagulant effects. The mechanism is unknown . Data have been conflicting. Case reports exist for propranolol, while acebutolol, atenolol, betaxolol, bisoprolol, carvedilol, esmolol, metoprolol, and pindolol reportedly do not interact. Hypoprothrombinemic effects are expected to be minimal. It is recommended that PT or INR be monitored when a beta-blocker is added to or deleted from the medical regimen of a patient receiving an oral anticoagulant. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122622/', '', 'Esmolol, Nebivolol, Carteolol, Methazolamide, Neostigmine, Acetylcholine, Physostigmine, Diclofenamide, Carteolol, Apraclonidine, Guanethidine, Nebivolol', 'Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122485, 'Warfarin', 'Tinidazole', 'Moderate', 'Coadministration with tinidazole may enhance the hypoprothrombinemic effect of warfarin and other oral anticoagulants. The interaction has been reported with metronidazole, another agent in the nitroimidazole class, and warfarin. Significant bleeding and elevation of prothrombin time have been observed. The proposed mechanism is metronidazole inhibition of CYP450 2C9, the isoenzyme responsible for the metabolic clearance of the more active S(-) enantiomer of warfarin. No data are available for tinidazole.', 'DDInter', 'Given its structural similarities to metronidazole, the potential for interaction with warfarin and other oral anticoagulants should be considered during coadministration with tinidazole. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation and for up to 8 days after discontinuation of tinidazole therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Metabolism', 'Given its structural similarities to metronidazole, the potential for interaction with warfarin and other oral anticoagulants should be considered during coadministration with tinidazole.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122623/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', 'Amikacin, Secnidazole, Vonoprazan, Bismuth subcitrate potassium, Bacitracin, Polymyxin B, Telavancin, Methenamine, Spectinomycin, Fosfomycin, Tedizolid, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122486, 'Warfarin', 'Tinzaparin', 'Major', 'Drugs that can affect hemostasis such as dextran, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or other anticoagulants may potentiate the risk of bleeding complications associated with the use of a low molecular weight heparin (LMWH), heparinoid, or fondaparinux. In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during LMWH, heparinoid, or fondaparinux therapy may also be increased by the concomitant use of other drugs that affect coagulation. The development of epidural and spinal hematoma can lead to long-term or permanent paralysis.', 'DDInter', 'In general, any agent that can enhance the risk of hemorrhage including other anticoagulants should be discontinued prior to initiation of LMWH, heparinoid, or fondaparinux therapy. If coadministration is necessary, it should be undertaken with caution and only after thorough assessment of risks and benefits. Close clinical and laboratory observation for bleeding complications is recommended. Patients undergoing neuraxial intervention and treated with these agents should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction. The optimal timing between the administration of anticoagulants and neuraxial procedures is not known.', 'Synergism', 'In general, any agent that can enhance the risk of hemorrhage including other anticoagulants should be discontinued prior to initiation of LMWH, heparinoid, or fondaparinux therapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122624/', '', 'Tinzaparin, Acenocoumarol, Selexipag', 'Antithrombin III human, Dicoumarol, Warfarin, Selexipag', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122487, 'Warfarin', 'Tipranavir', 'Major', 'Theoretically, tipranavir may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, high supplemental dosages of vitamin E, or agents that commonly cause thrombocytopenia.', 'DDInter', 'Caution is advised if tipranavir/ritonavir is used in combination with other drugs that affect hemostasis. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if tipranavir/ritonavir is used in combination with other drugs that affect hemostasis.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/122625/', '', 'Protein C, Acenocoumarol, Selexipag, Desirudin', 'Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Remdesivir, Doravirine, Bictegravir, More', 1767369485);
