INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (122897, 'Eribulin', 'Ketoconazole', 'Moderate', 'Eribulin may cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if eribulin is used in combination with other drugs that can prolong the QT interval. Baseline and periodic monitoring of electrocardiograms and serum electrolytes (potassium, magnesium) should be considered, and hypokalemia or hypomagnesemia corrected prior to initiating treatment with eribulin. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if eribulin is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123046/', '', 'Tamsulosin, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, More', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Mitotane, Selinexor, Aflibercept, More', 1767369485);
