INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (123003, 'Maraviroc', 'Ketoconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of maraviroc, which is primarily metabolized by the isoenzyme. Patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min) given maraviroc in combination with potent CYP450 3A4 inhibitors may have an increased risk of postural hypotension due to increased maraviroc exposure.', 'DDInter', 'Maraviroc should be administered at a dosage of 150 mg twice daily during coadministration with potent CYP450 3A4 inhibitors, including most protease inhibitors except tipranavir plus ritonavir. This dosage is recommended whether or not a concomitant CYP450 3A4 inducer (e.g., efavirenz, rifampin, carbamazepine, phenobarbital, phenytoin) is given. Patients with moderate hepatic impairment who receive 150 mg with a potent CYP450 3A4 inhibitor should be monitored closely for maraviroc-associated adverse events. Maraviroc is contraindicated for use in combination with potent CYP450 3A4 inhibitors in patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min).', 'Metabolism', 'Maraviroc should be administered at a dosage of 150 mg twice daily during coadministration with potent CYP450 3A4 inhibitors, including most protease inhibitors except tipranavir plus ritonavir.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123152/', '', 'Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, Clindamycin, Ciclopirox, More', 'Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Lenacapavir, Velpatasvir, Oseltamivir, Brincidofovir, Famc