INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (123007, 'Mephenytoin', 'Ketoconazole', 'Moderate', 'Phenytoin and other hydantoins may reduce itraconazole serum levels by up to 90%. The mechanism appears to be enhanced first-pass metabolism and hepatic enzyme metabolism (CYP450 3A4) of itraconazole. Itraconazole therapy may fail. Ketoconazole is affected in a similar manner. In addition, itraconazole may increase the plasma concentrations of phenytoin.', 'DDInter', 'Coadministration of phenytoin or other hydantoins with itraconazole or ketoconazole should be avoided from 2 weeks before and during treatment with itraconazole or ketoconazole. Some experts suggest that if concomitant use is unavoidable, a dosage increase for itraconazole or ketoconazole should be considered. In addition, monitoring of phenytoin concentrations is recommended and the dosage adjusted if needed. Fluconazole is eliminated unchanged by the kidneys and may be a more appropriate antifungal agent, depending on the patient''s condition.', 'Metabolism', 'Coadministration of phenytoin or other hydantoins with itraconazole or ketoconazole should be avoided from 2 weeks before and during treatment with itraconazole or ketoconazole.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123156/', '', 'Tiagabine, Rufinamide, Topiramate, Paramethadione, Methsuximide, Fenfluramine, Eslicarbazepine, Phensuximide, Lamotrigine, Methylphenobarbital, Phenacemide, More', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Lactic acid, Mitapivat, Methazolamide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (123008, 'Mesoridazine', 'Ketoconazole', 'Major', 'Mesoridazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Mesoridazine overdosage has been associated with ventricular arrhythmias and death. The extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s). In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with mesoridazine. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123157/', '', 'Loxapine, Molindone, Thiothixene', 'Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Clindamycin, Ciclopirox, Darunavir, More', 1767369485);
