INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (123577, 'Imipramine', 'Modafinil', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may increase the plasma concentrations of tricyclic antidepressants (TCAs) in patients who are deficient in CYP450 2D6 (approximately 7% to 10% of the Caucasian population; similar or lower in other populations). Modafinil and armodafinil are modest inhibitors of CYP450 2C19, an isoenzyme that provides an ancillary pathway for the metabolism of certain TCAs normally metabolized by CYP450 2D6 such as clomipramine or desipramine.', 'DDInter', 'Caution is advised if tricyclic antidepressants are coadministered with modafinil or armodafinil in CYP450 2D6-deficient patients. Pharmacologic effects and serum TCA levels should be monitored more closely whenever modafinil or armodafinil is added to or withdrawn from therapy, and the TCA dosage adjusted accordingly. Patients should be advised to notify their physician if they experience possible signs of TCA toxicity such as excessive drowsiness, restlessness, agitation, ataxia, blurred vision, tachycardia, arrhythmia, and seizures.', 'Metabolism', 'Caution is advised if tricyclic antidepressants are coadministered with modafinil or armodafinil in CYP450 2D6-deficient patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123726/', '', 'Maprotiline, Isocarboxazid, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran', 'Pemoline, Metamfetamine, Solriamfetol, Caffeine, Citicoline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (123578, 'Indacaterol', 'Modafinil', 'Moderate', 'Coadministration of beta-2 adrenergic agonists with other adrenergic agents may potentiate the risk of cardiovascular side effects. Beta-2 adrenergic agonists can produce clinically significant cardiovascular effects including increases in pulse rate and systolic or diastolic blood pressure as well as ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression. The risk is lower when beta-2 adrenergic agonists are inhaled at normally recommended dosages.', 'DDInter', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension. Blood pressure and heart rate should be closely monitored.', 'Synergism', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/123727/', '', 'Pemoline, Citicoline, Caffeine', 'Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Mometasone, Cromoglicic acid, Ipratropium, Beclomethasone dipropionate', 1767369485);
