INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (124101, 'Molindone', 'Metoprolol', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124251/', '', 'Acetylsalicylic acid, Ivabradine', 'Pimozide, Pimavanserin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (124102, 'Molindone', 'Metyrosine', 'Moderate', 'Coadministration of metyrosine in combination with haloperidol, phenothiazines, or other dopamine antagonists may increase the risk of extrapyramidal disorders. Extrapyramidal effects have been reported during metyrosine therapy. The mechanism is inhibition of catecholamine synthesis by metyrosine. In addition, sedative effects of both drugs may be increased.', 'DDInter', 'When concurrent use is required, patients should be monitored for extrapyramidal symptoms and instructed to notify their physician if they experience symptoms such as bradykinesia, hypertonia, rigidity, restlessness, and dysphagia. Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', 'When concurrent use is required, patients should be monitored for extrapyramidal symptoms and instructed to notify their physician if they experience symptoms such as bradykinesia, hypertonia, rigidity, restlessness, and dysphagia.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124252/', '', 'Macitentan, Riociguat, Tadalafil, Bosentan, Ambrisentan', 'Quetiapine, Asenapine, Pimavanserin, Ziprasidone, Clozapine, Olanzapine, Pimozide, Risperidone, Loxapine, Paliperidone', 1767369485);
