INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (124713, 'Aminophylline', 'Mycophenolate mofetil', 'Minor', 'In vitro studies have shown that mycophenolic acid in concentrations up to 100 mcg/mL decreases protein binding of theophylline from 53% to 45%. The clinical significance of this interaction is unknown. Decreased protein binding theoretically could lead to increased plasma theophylline concentrations. However, clinical toxicity has not been reported. It may be appropriate to monitor more closely patients receiving theophylline or other methylxanthine derivatives with mycophenolic acid products. Patients should be advised to contact their physician if they experience potential symptoms of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, and irregular heartbeat.', 'DDInter', '-', 'Distribution', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124863/', '', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Mepolizumab, Pentoxifylline, Zafirlukast, Bromotheophylline', '', 1767369485);
