INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (124841, 'Ustekinumab', 'Mycophenolate mofetil', 'Moderate', 'The use of ustekinumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy.', 'Others', 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124991/', '', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, Apremilast, Inebilizumab, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (124842, 'Valaciclovir', 'Mycophenolate mofetil', 'Minor', 'Coadministration with mycophenolic acid may increase the plasma concentrations of acyclovir. The mechanism of interaction has not been described. However, because both MPAG and acyclovir plasma concentrations are increased in the presence of renal impairment, the potential exists for the two drugs to compete for active tubular secretion, further increasing the concentrations of both drugs. Caution may be appropriate in patients with decreased renal function.', 'DDInter', '-', 'Excretion', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124992/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (124843, 'Valganciclovir', 'Mycophenolate mofetil', 'Moderate', 'Data from a single-dose study do not suggest the potential for a clinically significant pharmacokinetic interaction between mycophenolic acid and ganciclovir in patients with normal renal function. However, since ganciclovir and the glucuronide metabolite of mycophenolic acid are both eliminated primarily by the kidney, plasma levels of both may increase in renal impairment due to competition for renal tubular secretion. In addition, use of mycophenolic acid and ganciclovir may increase the risk and severity of hematologic toxicity due to additive myelosuppressive effects.', 'DDInter', 'Caution is advised if ganciclovir or its prodrug, valganciclovir, must be used concomitantly with mycophenolic acid products in patients with impaired renal function. Close monitoring for toxicities such as myelo- and immunosuppression is recommended.', 'Synergism', 'Caution is advised if ganciclovir or its prodrug, valganciclovir, must be used concomitantly with mycophenolic acid products in patients with impaired renal function.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/124993/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', '', 1767369485);
