INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (125448, 'Nadolol', 'Lasmiditan', 'Moderate', 'Coadministration of lasmiditan and agents that lower heart rate may increase the risk of bradycardia.', 'DDInter', 'Caution is advised during coadministration of lasmiditan and agents that lower heart rate if the estimated decrease in heart rate may present a clinical risk. Consider monitoring heart rate in patients for whom the estimated decrease in heart rate may not be tolerated.', 'Synergism', 'Caution is advised during coadministration of lasmiditan and agents that lower heart rate if the estimated decrease in heart rate may present a clinical risk.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/125600/', '', 'Sotalol, Sotalol', 'Rimegepant, Eptinezumab, Eletriptan, Ubrogepant, Naratriptan, Galcanezumab, Atogepant, Erenumab, Almotriptan, Fremanezumab', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (125449, 'Nadolol', 'Levamlodipine', 'Moderate', 'Additive reductions in heart rate, cardiac conduction, and cardiac contractility may occur when calcium channel blockers are used concomitantly with beta blockers, particularly in patients with ventricular or conduction abnormalities. While this combination may be useful and effective in some situations, potentially serious cardiovascular adverse effects such as congestive heart failure, severe hypotension, and/or exacerbation of angina may occur. The proposed mechanisms include additive slowing in AV conduction, reduced cardiac contractility secondary to beta-blockade, and decreased peripheral vascular resistance secondary to calcium channel blockade. In addition, some calcium channel blockers may inhibit the CYP450 metabolism of hepatically metabolized beta blockers, resulting in increased serum concentrations.', 'DDInter', 'Close clinical monitoring of patient hemodynamic response and tolerance is recommended if a calcium channel blocker is prescribed with a beta blocker, and the dosage of one or both agents adjusted as necessary. The same precaution should be observed when beta blocker ophthalmic solutions are used, since they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', 'Metabolism, Synergism', 'Close clinical monitoring of patient hemodynamic response and tolerance is recommended if a calcium channel blocker is prescribed with a beta blocker, and the dosage of one or both agents adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/125601/', '', 'Mibefradil', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (125450, 'Nadolol', 'Levobetaxolol (ophthalmic)', 'Moderate', 'Ophthalmic beta blocker solutions are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Additive effects of beta blockade may occur in patients receiving both oral and ophthalmic beta blocking agents.', 'DDInter', 'Blood pressure and intraocular pressure monitoring is recommended during concurrent use. Patients should be advised to notify their physician if they experience a slow pulse, irregular heartbeat, dizziness, shortness of breath, or syncope.', 'Synergism', 'Blood pressure and intraocular pressure monitoring is recommended during concurrent use.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/125602/', '', 'Carteolol', '', 1767369485);
