INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (126162, 'Eptifibatide', 'Ketoprofen', 'Moderate', 'The risk of bleeding associated with glycoprotein IIb/IIIa inhibitors is increased in the presence of nonsteroidal anti-inflammatory agents due to additive inhibitory effects on platelet function. Arterial puncture sites may be most problematic.', 'DDInter', 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126314/', '', 'Protein C, Acenocoumarol, Selexipag', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Tolazoline, Rabeprazole, Dimethyl sulfoxide', 1767369485);
