INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (126621, 'Repaglinide', 'Labetalol', 'Moderate', 'Beta-blockers may inhibit some of the normal physiologic response to hypoglycemia. Symptoms of hypoglycemia such as tremor and tachycardia may be absent, making it more difficult for patients to recognize an oncoming episode. Specifically, inhibition of catecholamine-mediated glycogenolysis and glucose mobilization in association with beta-blockade can potentiate insulin-induced hypoglycemia in diabetics and delay the recovery of normal blood glucose levels. Prolonged and severe hypoglycemia may occur, although these events have rarely been reported. Significant increases in blood pressure and bradycardia can also occur during hypoglycemia in diabetics treated with insulin and beta-blockers due to antagonism of epinephrine''s effect on beta-2 adrenergic receptors, which leads to unopposed alpha-adrenergic effects including vasoconstriction. Other effects reported with various beta-blockers include decreased glucose tolerance and decreased glucose-induced insulin secretion.', 'DDInter', 'In general, cardioselective beta-blockers are considered safer than noncardioselective agents in the treatment of diabetic patients. Patients should be advised of the need for regular blood glucose monitoring and be aware that certain symptoms of hypoglycemia such as tremor and tachycardia may be masked. However, other symptoms such as headache, dizziness, drowsiness, confusion, nausea, hunger, weakness, and perspiration may be unaffected.', 'Others', 'In general, cardioselective beta-blockers are considered safer than noncardioselective agents in the treatment of diabetic patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126775/', '', 'Lixisenatide, Albiglutide, Guar gum, Acarbose, Pramlintide, Sitagliptin, Alogliptin, Liraglutide, Troglitazone, Semaglutide, Linagliptin, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (126622, 'Reserpine', 'Labetalol', 'Moderate', 'Sympathetic ganglion-blocking or catecholamine-depleting agents such as guanethidine, reserpine, and monoamine oxidase (MAO) inhibitors may potentiate the pharmacologic effects of beta-blockers, which are thought to competitively antagonize catecholamines at cardiac and other peripheral adrenergic neurons. Combining these medications may increase the risk of hypotension, orthostasis, bradycardia, and heart failure due to excessive reduction of sympathetic activity.', 'DDInter', 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents. Patients should contact their doctor if they experience dizziness, lightheadedness, syncope, bradycardia, shortness of breath, difficulty breathing, edema, and/or chest pain.', 'Synergism', 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126776/', '', 'Deserpidine, Deserpidine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (126623, 'Rifampicin', 'Labetalol', 'Moderate', 'Rifampin may decrease serum levels and effects of some oral beta-blockers. The proposed mechanism is increased CYP450 oxidative metabolism of beta-blockers that are hepatically metabolized. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact.', 'DDInter', 'Patients should be monitored for clinical response and tolerance when rifampin is added to or discontinued from a beta-blocker regimen. The beta-blocker dosage may require adjustment.', 'Metabolism', 'Patients should be monitored for clinical response and tolerance when rifampin is added to or discontinued from a beta-blocker regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126777/', '', 'Nadolol, Nebivolol, Sotalol, Pindolol, Nadolol, Esmolol, Betaxolol, Nebivolol, Carteolol, Sotalol, Pindolol', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Rifapentine, Streptomycin, Trimethoprim, Cycloserine, Rifabutin, Isoniazid, Ethambutol, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (126624, 'Riociguat', 'Labetalol', 'Moderate', 'Riociguat may potentiate the action of antihypertensive agents and other vasodilators. Symptomatic hypotension may occur. Riociguat stimulates soluble guanylate cyclase (sGC), an enzyme in the cardiopulmonary system that binds with nitric oxide (NO) to catalyze the synthesis of cyclic guanosine monophosphate (cGMP). The resultant accumulation of cGMP in vascular smooth muscle cells leads to peripheral vasodilation and blood pressure decreases.', 'DDInter', 'Caution is advised if riociguat is prescribed in combination with antihypertensive agents or other vasodilators.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126778/', '', 'Bosentan, Ambrisentan, Macitentan, Metyrosine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (126625, 'Ritodrine', 'Labetalol', 'Major', 'The use of a nonselective beta blocker with ritodrine (a beta-2 agonist) may result in mutually antagonistic effects, negating the effect of one or both drugs.', 'DDInter', 'In general, the use of beta blockers with ritodrine is discouraged. Selective beta blockers (e.g. acebutolol, atenolol, betaxolol, bisoprolol, metoprolol) may be preferable if the use of a beta blocker is unavoidable.', 'Antagonism', 'In general, the use of beta blockers with ritodrine is discouraged.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126779/', '', 'Bremelanotide, Flibanserin', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (126626, 'Rivastigmine', 'Labetalol', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', 'DDInter', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126780/', '', 'Memantine, Ginkgo biloba, Aducanumab', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (126627, 'Rocuronium', 'Labetalol', 'Moderate', 'Neuromuscular blocking agents and beta-blockers may have additive cardiovascular depressant effects. Limited data suggest that beta-blockers may modestly potentiate the pharmacologic effects of neuromuscular blocking agents. The exact mechanism of interaction is unknown but may involve effects in the postsynaptic membrane, as beta-blockers alone have been reported to exacerbate or unmask myasthenia gravis.', 'DDInter', 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers. Respiratory and cardiovascular status should be closely monitored.', 'Synergism', 'Clinicians should recognize the potential for altered effects of neuromuscular blocking agents in the presence of beta-blockers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126781/', '', 'Botulinum toxin type A', '', 1767369485);
