INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (126775, 'Disopyramide', 'Lamotrigine', 'Moderate', 'In vitro studies have shown lamotrigine exhibits class IB antiarrhythmic activity at therapeutically relevant concentrations. Based on this data, there is a concern that concomitant use of other sodium channel blockers may increase the risk of proarrhythmias. In healthy individuals, a thorough QT study did not show slowed ventricular conduction (widen QRS); however, slow ventricular conduction and proarrhythmias may occur in patients with structural heart disease or myocardial ischemia.', 'DDInter', 'Caution and clinical monitoring are recommended if concomitant use of other sodium channel blockers is needed as this may increase the risk of proarrhythmias. The manufacturer recommends lamotrigine be avoided in patients who have cardiac conduction disorders (e.g., second- or third-degree heart block), ventricular arrhythmias, or cardiac disease or abnormality (e.g., myocardial ischemia, heart failure, structural heart disease, Brugada syndrome or other sodium channelopathies) as use may increase the risk of proarrhythmias including sudden death. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate an irregular heart rhythm such as dizziness, lightheadedness, fainting, palpitation, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended if concomitant use of other sodium channel blockers is needed as this may increase the risk of proarrhythmias.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126929/', '', 'Bretylium, Dronedarone, Ibutilide', 'Brivaracetam, Cenobamate, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Clonazepam, Paramethadione, Carbamazepine, Perampanel, Ganaxolone, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (126776, 'Escitalopram', 'Lamotrigine', 'Moderate', 'The efficacy of anticonvulsants may be diminished during coadministration with selective serotonin reuptake inhibitors (SSRIs) or serotonin-norepinephrine reuptake inhibitor (SNRIs). Antidepressants including SSRIs and SNRIs can reduce seizure threshold.Coadministration of SSRIs or SNRIs with some anticonvulsants, particularly carbamazepine, eslicarbazepine, oxcarbazepine and valproic acid, may increase the risk of hyponatremia. The risk appears to be dose-related, and elderly patients and patients who are volume depleted (e.g., diuretic use) may be at greater risk.', 'DDInter', 'SSRIs and SNRIs should be avoided in patients with unstable epilepsy, and used cautiously in patients with epilepsy controlled with anticonvulsant medications. Treatment with SSRIs and SNRIs should be discontinued if seizures develop or seizure frequency increases. Patients receiving SSRIs or SNRIs with anticonvulsants, particularly carbamazepine, eslicarbazepine, oxcarbazepine and/or valproic acid, should also have serum sodium levels measured regularly and monitored for development of hyponatremia, particularly when higher dosages of these medications are used. Signs and symptoms of hyponatremia include nausea, vomiting, headache, difficulty concentrating, memory impairment, confusion, malaise, lethargy, muscle weakness or spasms, and unsteadiness. In more severe and/or acute cases, hallucination, syncope, seizure, coma, respiratory arrest, and death may occur.', 'Antagonism', 'SSRIs and SNRIs should be avoided in patients with unstable epilepsy, and used cautiously in patients with epilepsy controlled with anticonvulsant medications.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126930/', '', 'Oxitriptan, Viloxazine, St. John''s Wort, Tryptophan', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (126777, 'Esketamine', 'Lamotrigine', 'Moderate', 'Concomitant use of esketamine with central nervous system (CNS) depressants may increase sedation and impairment of attention, judgment, thinking, reaction speed, and psychomotor skills.', 'DDInter', 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants. Due to the risk of delayed or prolonged sedation and other adverse effects, patients should be monitored for at least 2 hours after esketamine administration, followed by an assessment to determine when the patient is considered clinically stable and ready to leave the healthcare setting. Patients should be instructed not to engage in potentially hazardous activities, such as driving a motor vehicle or operating machinery, until the next day after a restful sleep.', 'Synergism', 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/126931/', '', 'Oxitriptan, Viloxazine, St. John''s Wort, Tryptophan, Halothane, Thiopental, Isoflurane, Nitrous oxide, Desflurane, Enflurane, Sevoflurane, Etomidate', '', 1767369485);
