INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (127303, 'Paclitaxel (protein-bound)', 'Lapatinib', 'Moderate', 'Coadministration with lapatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter. The mechanism is decreased clearance via these routes due to inhibition by lapatinib.', 'DDInter', 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lapatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/127457/', '', 'Acalabrutinib, Dacomitinib, Avapritinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, More', '', 1767369485);
