INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (128866, 'Coccidioides immitis spherule', 'Lenalidomide', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129020/', '', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Emapalumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (128867, 'Conjugated estrogens (topical)', 'Lenalidomide', 'Major', 'Concomitant treatment with agents that can cause thrombosis such as erythropoiesis-stimulating agents, estrogens, selective estrogen receptor modulators, or C1 esterase inhibitors may potentiate the risk of venous thromboembolic events associated with the use of lenalidomide. Deep vein thrombosis (DVT) and pulmonary embolism (PE) have been observed at significantly increased rates when lenalidomide was coadministered with dexamethasone for the treatment of multiple myeloma.', 'DDInter', 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving lenalidomide with dexamethasone. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain; shortness of breath; rapid pulse; pain, swelling, and/or discoloration in an arm or leg; and numbness or weakness on one side of the body. It is not known whether prophylactic anticoagulation or antiplatelet therapy may lessen the risk of venous thromboembolic events. The decision to take prophylactic measures should be done carefully after a thorough assessment of underlying risk factors. If a thromboembolic event occurs during therapy with lenalidomide, treatment must be discontinued and standard anticoagulation therapy initiated. Once anticoagulation is stabilized and complications of the thromboembolic event under control, lenalidomide may be restarted at the original dose if benefit is deemed to outweigh the risks. Anticoagulation therapy should be continued during the remaining course of lenalidomide treatment.', 'Synergism', 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving lenalidomide with dexamethasone.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129021/', '', 'Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, More', '', 1767369485);
