INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (129647, 'Lithium carbonate', 'Levamlodipine', 'Moderate', 'Several case reports have suggested that the combination of lithium and calcium channel blockers may cause neurotoxicity and psychiatric effects such as psychosis. The mechanism is unknown. Data have been conflicting. Cases have been reported for diltiazem and verapamil.', 'DDInter', 'Patients who must take both drugs should be closely monitored for adverse neurologic and psychiatric effects. Patients should be advised to notify their physician if they experience symptoms of possible lithium toxicity such as drowsiness, dizziness, confusion, ataxia, vomiting, diarrhea, thirst, blurry vision, tinnitus, or tremor.', 'Others', 'Patients who must take both drugs should be closely monitored for adverse neurologic and psychiatric effects.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/129801/', '', '', '', 1767369485);
