INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (131380, 'Interferon alfa-2a, Recombinant', 'Lisinopril', 'Moderate', 'The concomitant use of recombinant interferon-alpha-2a and some angiotensin converting enzyme (ACE) inhibitors has been associated with a higher incidence of granulocytopenia than either drug used alone in patients with cryoglobulinemia. The mechanism is unknown but may be related to an autoimmune response. It is also unknown whether patients with other conditions may be affected. Data exist for enalapril and captopril.', 'DDInter', 'Until more information is available, close hematologic monitoring is recommended.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131537/', '', 'Felodipine, Amlodipine, Verapamil, Bisoprolol, Hydrochlorothiazide, Indapamide, Amlodipine, Valsartan, Niacin, Acetylsalicylic acid, Ezetimibe, More', '', 1767369485);
