INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (131382, 'Lithium carbonate', 'Lisinopril', 'Major', 'Coadministration with angiotensin converting enzyme (ACE) inhibitors may increase serum lithium concentrations and the risk for lithium toxicity. Several mechanisms may be involved, one of which is reduced renal lithium clearance due to natriuresis secondary to the inhibition of aldosterone and angiotensin II by ACE inhibitors. The combination may also cause renal dysfunction secondary to volume depletion during chronic therapy, which can further impair lithium clearance. The interaction was suspected in cases of lithium toxicity that occurred up to several weeks after the initiation of ACE inhibitor therapy.', 'DDInter', 'Given the narrow therapeutic index of lithium, caution is advised during coadministration with ACE inhibitors, particularly in the elderly or patients with other risk factors (e.g., sodium restriction, renal impairment, congestive heart failure, dehydration, concomitant use of diuretics or NSAIDs). Pharmacologic response and serum lithium levels should be monitored more closely whenever an ACE inhibitor is added to or withdrawn from therapy, and the lithium dosage adjusted as necessary. Empiric reductions of both drugs may be appropriate during initial therapy. Renal function should also be monitored regularly. Patients should be advised to seek medical attention if they experience potential signs and symptoms of lithium toxicity such as drowsiness, dizziness, confusion, muscle weakness, vomiting, diarrhea, polydipsia, polyuria, tinnitus, tremor, ataxia, and blurred vision.', 'Excretion', 'Given the narrow therapeutic index of lithium, caution is advised during coadministration with ACE inhibitors, particularly in the elderly or patients with other risk factors (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131539/', '', 'Atorvastatin, Simvastatin, Lovastatin, Rosuvastatin, Niacin, Pravastatin, Ezetimibe, Bempedoic acid, Fenofibrate', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (131383, 'Lofexidine', 'Lisinopril', 'Moderate', 'The hypotensive effects of lofexidine and antihypertensive agents may be additive. Postural hypotension may occur.', 'DDInter', 'Hemodynamic responses should be monitored during coadministration of lofexidine with antihypertensive agents, especially during the first few weeks of therapy.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131540/', '', 'Atorvastatin, Simvastatin, Lovastatin, Rosuvastatin, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Fenofibrate', 'Nalmefene, Disulfiram, Levacetylmethadol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (131384, 'Lorazepam', 'Lisinopril', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131541/', '', 'Halazepam', 'Bempedoic acid', 1767369485);
