INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (131728, 'Procainamide', 'Lithium carbonate', 'Major', 'Class IA (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks. Caution and clinical monitoring are recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131885/', '', 'Lidocaine, Tocainide, Mexiletine, Bretylium, Moricizine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (131729, 'Promazine', 'Lithium carbonate', 'Moderate', 'Although lithium and phenothiazines are frequently used together in the setting of acute mania, concurrent administration may induce a number of neurologic and psychiatric effects (despite decreased phenothiazine levels during lithium therapy). Mental status changes, delirium, seizures, extrapyramidal effects, fever, and other symptoms have been reported.', 'DDInter', 'Patients receiving lithium and a phenothiazine should be closely monitored for altered efficacy and safety. In some circumstances, discontinuation of one or both agents may be necessary.', 'Synergism', 'Patients receiving lithium and a phenothiazine should be closely monitored for altered efficacy and safety.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131886/', '', '', '', 1767369485);
