INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (131732, 'Propranolol', 'Lithium carbonate', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131889/', '', 'Penbutolol, Carteolol', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (131733, 'Protriptyline', 'Lithium carbonate', 'Moderate', 'The concomitant use of lithium and tricyclic antidepressants (TCAs) may potentiate the risk of neurotoxicity and psychotic symptoms, even at therapeutic serum lithium levels. The mechanism is unknown but may be related to synergistic effects on neurotransmission. Mania, myoclonus, tremors, memory difficulties, confusion, serotonin syndrome, and a neuroleptic malignant-like syndrome have been reported in patients treated with TCAs several days after the addition of lithium, in most cases requiring discontinuation of either the TCA or lithium. Elderly patients may be particularly susceptible to this interaction.', 'DDInter', 'Close monitoring for symptoms of neurotoxicity is recommended if TCAs are used with lithium. Dosage adjustment or discontinuation of one or both drugs may be necessary if an interaction is suspected.', 'Synergism', 'Close monitoring for symptoms of neurotoxicity is recommended if TCAs are used with lithium.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131890/', '', 'Viloxazine', '', 1767369485);
