INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (131762, 'Sarecycline', 'Lithium carbonate', 'Minor', 'Coadministration with tetracyclines may increase the plasma concentrations of lithium and the risk of toxicity. In addition, cases of pseudotumor cerebri have been reported following administration of minocycline with lithium. The mechanism is unknown; however, a proposed mechanism suggests that tetracyclines may adversely affect the renal clearance of lithium. The clinical relevance of this interaction has not been determined. A dosage adjustment for lithium may be required in patients who must take both drugs concomitantly and patients should be monitored for clinical and laboratory evidence of lithium toxicity. Patients should be advised to notify their doctor if they experience symptoms of possible lithium toxicity such as drowsiness, dizziness, weakness, ataxia, vomiting, diarrhea, thirst, blurry vision, tinnitus, tremor, or increased urination.', 'DDInter', '-', 'Excretion', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131919/', '', 'Tigecycline', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (131763, 'Sargramostim', 'Lithium carbonate', 'Moderate', 'Lithium may potentiate the myeloproliferative effects of colony-stimulating factors (CSF). In vitro and in vivo evidence suggests that lithium can induce the production and release of CSF in some patients, resulting in leukocytosis that is frequently due to neutrophilia. Significant increases in CD34+ cells and thrombocytes have also been reported.', 'DDInter', 'Patients receiving concomitant treatment with colony-stimulating factors and lithium may require more frequent monitoring of blood counts in order to avoid the potential complications of excessive leukocytosis.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/131920/', '', 'Interferon alfa-n1, Oprelvekin, Sipuleucel-T, Elapegademase, Interferon beta-1b, Peginterferon alfa-2a, Ropeginterferon alfa-2b, Interferon alfacon-1, Peginterferon beta-1a, Interferon beta-1a, Aldesleukin, More', '', 1767369485);
