INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (132123, 'Perflutren', 'Lofexidine', 'Moderate', 'Perflutren lipid microsphere suspension (Definity) has been reported to cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if perflutren lipid microsphere suspension is used in combination with other drugs that can prolong the QT interval. Please refer to the prescribing information for these drugs for more information. No data are available regarding the effect of perflutren protein-type A microsphere suspension (Optison) on the QT interval of the electrocardiogram.', 'Synergism', 'Caution is recommended if perflutren lipid microsphere suspension is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/132280/', '', 'Diamorphine, Acamprosate, Naltrexone, Varenicline, Disulfiram, Nalmefene, Nicotine', '', 1767369485);
