INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (132999, 'Pantoprazole', 'Lovastatin', 'Moderate', 'A case report suggests that coadministration with esomeprazole may increase the plasma concentrations of atorvastatin and the associated risk of myopathy. The proposed mechanism is competitive inhibition of intestinal P-glycoprotein, resulting in decreased drug secretion into the intestinal lumen and increased drug bioavailability. Another, perhaps minor mechanism is competitive inhibition of CYP450 3A4 metabolism.', 'DDInter', 'Because of the increased risk of musculoskeletal toxicity associated with high levels of HMG-CoA reductase inhibitory activity in plasma, patients treated with atorvastatin, lovastatin, simvastatin, and red yeast rice (which contains lovastatin) should be monitored more closely during concomitant use of proton pump inhibitors. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report to their physician any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated or if myopathy is suspected or diagnosed.', 'Metabolism, Absorption', 'Because of the increased risk of musculoskeletal toxicity associated with high levels of HMG-CoA reductase inhibitory activity in plasma, patients treated with atorvastatin, lovastatin, simvastatin, and red yeast rice (which contains lovastatin) should be monitored more closely during concomitant use of proton pump inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/133156/', '', 'Levofloxacin, Bismuth subcitrate potassium, Dexlansoprazole, Rifabutin', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Clofibrate, Lomitapide, Colestipol, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (133000, 'Pazopanib', 'Lovastatin', 'Moderate', 'Concomitant use of pazopanib with simvastatin may increase the risk of ALT elevations, possibly due to additive hepatotoxic effects.', 'DDInter', 'Caution is advised if pazopanib is used in combination with simvastatin. Serum liver transaminases and bilirubin should be measured prior to initiation of pazopanib and regularly during treatment as recommended in the product labeling. If ALT elevations occur, simvastatin should be discontinued and guidelines for pazopanib dosage adjustments should be followed, or alternatives to pazopanib considered. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, light colored stools, and jaundice. Insufficient data are available to assess the risk of pazopanib administered concomitantly with other HMG-CoA reductase inhibitors.', 'Synergism', 'Caution is advised if pazopanib is used in combination with simvastatin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/133157/', '', 'Gemfibrozil, Fenofibric acid, Evinacumab, Evolocumab, Dextrothyroxine, Cholestyramine, Ezetimibe, Clofibrate, Colestipol, Niacin, Fenofibrate, More', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Vemurafenib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, More', 1767369485);
