INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (134877, 'Echinacea', 'Maraviroc', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', 'DDInter', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135034/', '', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Dolutegravir, Oseltamivir, Acyclovir, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (134878, 'Enzalutamide', 'Maraviroc', 'Major', 'Patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min) given maraviroc may have an increased risk of postural hypotension due to increased maraviroc exposure. Moreover, these patients often have cardiovascular comorbidities that could predispose them to adverse cardiovascular events triggered by postural hypotension. Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of maraviroc, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Maraviroc should be administered at a dosage of 600 mg twice daily during coadministration with potent CYP450 3A4 inducers. However, if a potent CYP450 3A4 inhibitor such as itraconazole, ketoconazole, delavirdine, clarithromycin, telithromycin, nefazodone, or any protease inhibitor (except tipranavir plus ritonavir) is also used in combination with the inducer, then maraviroc dosage should be reduced to 150 mg twice daily. Maraviroc is contraindicated for use with potent CYP450 3A4 inducers in patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min).', 'Metabolism', 'Maraviroc should be administered at a dosage of 600 mg twice daily during coadministration with potent CYP450 3A4 inducers.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/135035/', '', 'Lamivudine, Cabotegravir, Zanamivir, Valaciclovir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Elbasvir, Famciclovir, Voxilaprevir, More', 'Darolutamide, Anastrozole, Letrozole, Toremifene, Fulvestrant, Abarelix, Flutamide, Relugolix, Degarelix, Nilutamide, Abiraterone, Exemestane', 1767369485);
